function DecrementActiveSchedule(event)
{
	var target = event.data;
	target.ActiveSchedule--;
	target.RefreshActiveScheduleVal();
	
	if (ValidSchedules.length) { DrawSchedule(target); }
}
function IncrementActiveSchedule(event)
{
	var target = event.data;
	target.ActiveSchedule++;
	target.RefreshActiveScheduleVal();
	
	if (ValidSchedules.length) { DrawSchedule(target); }
}

